// src/BubbleChat.js
import React, { useState } from 'react';
import 'tailwindcss/tailwind.css'; // 여기에 추가

const BubbleChat = (props) => {
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);

  const handleNextClick = () => {
    if (currentMessageIndex < messages.length - 1) {
      setCurrentMessageIndex(currentMessageIndex + 1);
    }
  };

  return (
    <div className="flex justify-center items-end">
      <div className="bg-green-500 text-white p-5 rounded-lg mb-8">
        <p>{props.messages[currentMessageIndex]}</p>
        {currentMessageIndex < props.messages.length - 1 && (
          <button
            className="bg-white text-green-500 border-none px-4 py-2 mt-4 cursor-pointer"
            onClick={handleNextClick}
          >
            다음
          </button>
        )}
      </div>
    </div>
  );
};

export default BubbleChat;
