import React, { useState } from 'react';
import 'tailwindcss/tailwind.css';

const BubbleChat = (props) => {
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);

  const handleNextClick = () => {
    if (currentMessageIndex < props.messages.length - 1) {
      setCurrentMessageIndex(currentMessageIndex + 1);
    }
  };

  return (
    <div className="flex justify-center items-end w-full relative">
        <img src={props.img} className="w-48 absolute -right-2 bottom-0" alt="logo" />
      <div className="bg-white shadow-md ring-1 ring-inset ring-gray-300 text-grey p-5 rounded-lg mb-16 w-4/5 max-w-5xl">
        <p>{props.messages[currentMessageIndex]}</p>
        {currentMessageIndex < props.messages.length - 1 && (
          <button
            className="bg-purple-300 text-white border-none px-4 py-2 mt-4 cursor-pointer rounded-lg"
            onClick={handleNextClick}
          >
            다음
          </button>
        )}
      </div>
    </div>
  );
};

export default BubbleChat;
